var parameters = {
	"id":"ggb",
	"width":900,
	"height":600,
	"showToolBar":false,
	"borderColor":null,
	"showMenuBar":false,
	"showAlgebraInput":false,
	"showResetIcon":false,
	"enableLabelDrags":false,
	"enableShiftDragZoom":false,
	"enableRightClick":false,
	"capturingThreshold":null,
	"showToolBarHelp":false,
	"errorDialogsActive":true,
	"useBrowserForJS":true,
	"ggbBase64":""};
var applet = new GGBApplet(parameters, '5.0');

var ggbInit = false;

window.onload = function() {
	applet.inject('applet_container');
	document.getElementsByTagName('select')[0].selectedIndex = 0;
	document.getElementById('radioTipo').checked = true;
	document.getElementById('densidade').checked = false;
	document.getElementById('sizeq2').value = 1;
}

function ggbOnInit(){
	if(!ggbInit){
		ggbInit = true;
		campo('pn');
	}
}

function geogebra(comando){
	document.ggb.evalCommand(comando);
}

function visualizacao(tipo){
	var dipolo = document.getElementsByTagName('select')[0].value;
	switch(tipo){
		case 'campo-vetorial':
		campo(dipolo);
		displayForm('inline-block');
		break;
		case 'linhas-campo':
		linhas();
		displayForm('none');
		break;
	}
}

function campo(){
	document.ggb.reset();
	var dipolo = document.getElementsByTagName('select')[0].value;
	var sizeq2 = document.getElementById('sizeq2').value;
	var pointSize = 4 + parseInt(document.getElementById('sizeq2').value);
	var d = document.getElementById('densidade').checked ? 1 : 2;
	allInvisible();
	if(dipolo=='pn'){
		document.ggb.setVisible('A',true);
		document.ggb.setVisible('B',true);
		document.ggb.setPointSize('B',pointSize);
		for(var x=-15; x<=16; x+=d){
			for(var y=10; y>=-11; y-=d){
				geogebra('Translate(UnitVector(Vector(A,('+x+','+y+'))/Distance(('+x+','+y+'),A)^2+'+sizeq2+'*Vector(('+x+','+y+'),B)/Distance(('+x+','+y+'),B)^2),('+x+','+y+'))');
			}
		}
	}
	if(dipolo=='pp'){
		document.ggb.setVisible('A',true);
		document.ggb.setVisible('C',true);
		document.ggb.setPointSize('C',pointSize);
		for(var x=-15; x<=16; x+=d){
			for(var y=10; y>=-11; y-=d){
				geogebra('Translate(UnitVector(Vector(A,('+x+','+y+'))/Distance(('+x+','+y+'),A)^2+'+sizeq2+'*Vector(C,('+x+','+y+'))/Distance(('+x+','+y+'),C)^2),('+x+','+y+'))');
			}
		}
	}
	if(dipolo=='nn'){
		document.ggb.setVisible('B',true);
		document.ggb.setVisible('D',true);
		document.ggb.setPointSize('B',pointSize);
		for(var x=-15; x<=16; x+=d){
			for(var y=10; y>=-11; y-=d){
				geogebra('Translate(UnitVector('+sizeq2+'*Vector(('+x+','+y+'),B)/Distance(('+x+','+y+'),B)^2+Vector(('+x+','+y+'),D)/Distance(('+x+','+y+'),D)^2),('+x+','+y+'))');
			}
		}
	}
}


function linhas(dipolo){
	document.ggb.reset();
	geogebra('R=Translate(A,0.001*Vector(A,B))');
	geogebra('M=Midpoint(A,B)');
	geogebra('L=Segment(A,B)');
	for(var g=20; g<180; g+=20){
		geogebra('linha'+g+'=CircumcircularArc(A,Rotate(R,'+g+'°,A),B)');
		geogebra('Reflect(linha'+g+',Rotate(L,90°,M))');		
	}

}

function allInvisible(){
	document.ggb.setVisible('A',false);
	document.ggb.setVisible('B',false);
	document.ggb.setVisible('C',false);
	document.ggb.setVisible('D',false);
}

function displayForm(display){
	document.getElementById('size').style.display = display;
	document.getElementById('dipolo').style.display = display;
	document.getElementById('densidade').style.display = display;
	document.getElementById('span-densidade').style.display = display;
}

