var parameters = {
	"id":"ggb",
	"width":500,
	"height":500,
	"showToolBar":false,
	"borderColor":null,
	"showMenuBar":false,
	"showAlgebraInput":false,
	"showResetIcon":false,
	"enableLabelDrags":false,
	"enableShiftDragZoom":false,
	"enableRightClick":false,
	"capturingThreshold":null,
	"showToolBarHelp":false,
	"errorDialogsActive":true,
	"useBrowserForJS":true,
	"ggbBase64":""};
var applet = new GGBApplet(parameters, '5.0');

var ggbInit = false;

window.onload = function() {
	applet.inject('applet_container');
	document.getElementsByTagName('select')[0].selectedIndex = 0;
}

function ggbOnInit(){
	document.ggb.setCoordSystem(-4,4,-4,4);
	if(!ggbInit){
		campo('circular');
		ggbInit = true;
	}
}

function geogebra(comando){
	document.ggb.evalCommand(comando);
}

function campo(tipo){
	if(ggbInit)
		document.ggb.reset();
	document.ggb.setAnimating('angulo',false);
	document.ggb.setAnimating('slider',false);
	switch(tipo){
		case 'circular':
		circular();
		break;
		case 'expansivo':
		expansivo();
		break;
		case 'radial':
		radial();
		break;
	}
	document.ggb.startAnimation();
}

function circular(){
	sequenceRotate('P1',45);
	sequenceRotate('P2',30);
	sequenceRotate('P3',20);
	sequenceRotate('P4',15);
	sequenceRotate('P5',10);
	document.ggb.setAnimating('angulo',true);
}

function sequenceRotate(ponto,graus){
	for(var g=graus; g<=360; g+=graus*2){
		geogebra('Vector(Rotate('+ponto+',angulo+'+(g-graus)+'°),Rotate('+ponto+',angulo+'+g+'°))');
	}
}

function expansivo(){
	geogebra('b0 = slider');
	for(var g=0; g<360; g+=30){
		geogebra('If(b0 < 1, Vector((0, 0), Rotate((b0, 0),'+g+'°)), Vector(Rotate((a0, 0),'+g+'°), Rotate((b0, 0),'+g+'°)))');
		geogebra('Vector(Rotate((a1, 0),'+g+'°), Rotate((b1, 0),'+g+'°))');
		geogebra('Vector(Rotate((a2, 0),'+g+'°), Rotate((b2, 0),'+g+'°))');
	}
	document.ggb.setAnimating('slider',true);
}

function radial(){
	geogebra('b0 = 2-slider');
	for(var g=0; g<360; g+=30){
		geogebra('If(b0 < 1, Vector(Rotate((b0, 0),'+g+'°),(0, 0)), Vector(Rotate((b0, 0),'+g+'°), Rotate((a0, 0),'+g+'°)))');
		geogebra('Vector(Rotate((b1, 0),'+g+'°), Rotate((a1, 0),'+g+'°))');
		geogebra('Vector(Rotate((b2, 0),'+g+'°), Rotate((a2, 0),'+g+'°))');
	}
	document.ggb.setAnimating('slider',true);
}