var terceiroVetor = false;

//Cálculo com 2 vetores
function verificar2(){
	ggb = document.hxxS8wmw;
	var entrada2 = document.getElementById('entrada2');
	var coordenadasV = entrada2.elements[0].value;
	var coordenadasU = entrada2.elements[1].value;

	if(!coordenadasV || !coordenadasU || !coordenadasValida(coordenadasV) || !coordenadasValida(coordenadasU)){
		alert('ENTRADA INVÁLIDA\nPor favor, adicione as coordenadas númericas ( x , y ) aos vetores separadas por vírcula');
		return;
	}

	ggb.evalCommand('v = ('+coordenadasV+')');
	ggb.evalCommand('u = ('+coordenadasU+')');
	ggb.setVisible('v',true);
	ggb.setVisible('u',true);
	ggb.setVisible('w',false);

	//Armazenar os valores digitados
	vx = ggb.getXcoord('v');
	vy = ggb.getYcoord('v');
	ux = ggb.getXcoord('u');
	uy = ggb.getYcoord('u');
	document.getElementById("vx").innerHTML = vx;
	document.getElementById("vy").innerHTML = vy;
	document.getElementById("ux").innerHTML = ux;
	document.getElementById("uy").innerHTML = uy;
	
	//Dados dos vetores
	moduloV = Math.sqrt(Math.pow(vx,2)+Math.pow(vy,2));
	moduloU = Math.sqrt(Math.pow(ux,2)+Math.pow(uy,2));
	anguloV = Math.atan2(vy,vx);
	anguloU = Math.atan2(uy,ux);

	var escala = maiorModulo() + 0.2*maiorModulo();
	ggb.setCoordSystem(-escala,escala,-escala,escala);

	if(anguloV<0){
		anguloV += 2*Math.PI;
	}
	if(anguloU<0){
		anguloU += 2*Math.PI;
	}

	//Mostrar parágrafo da solução
	document.getElementById("resposta2").style.display = 'block';
	document.getElementById('multiplos').style.display = 'none';
	document.getElementById('naoMultiplos').style.display = 'none';
	document.getElementById('vetorNulo').style.display = 'none';
	document.getElementById('prova2').style.display = 'none';
	document.getElementById('resposta3').style.display = 'none';

	//Solucão para dois vetores
	prova('doisVetores');
}

//Cálculo com 3 vetores
function verificar3(){
	terceiroVetor = true;
	
	var entrada3 = document.getElementById('entrada3');
	var coordenadasW = entrada3.elements[0].value;
	if(!coordenadasW || !coordenadasValida(coordenadasW)){
		alert('ENTRADA INVÁLIDA\nPor favor, adicione as coordenadas númericas ( x , y ) ao vetor separadas por vírcula');
		return;
	}

	ggb.evalCommand('w = ('+coordenadasW+')');
	ggb.setVisible('w',true);

	wx = ggb.getXcoord('w');
	wy = ggb.getYcoord('w');
	moduloW = Math.sqrt(Math.pow(wx,2)+Math.pow(wy,2));
	anguloW = Math.atan2(wy,wx);
	var escala = maiorModulo() + 0.2*maiorModulo();
	ggb.setCoordSystem(-escala,escala,-escala,escala);

	if(anguloW<0){
		anguloW += 2*Math.PI;
	}

	document.getElementById("wx").innerHTML = wx;
	document.getElementById("wy").innerHTML = wy;
	document.getElementById("resposta3").style.display = 'block';
	document.getElementById("prova3").style.display = 'block';

	if(vetorNulo())
		prova('vetorNulo');
	else if(angulosIguais())
		prova('angulosIguais');
	else{
		escalonar();
		prova('combinacaoLinear');
	}
}

//Escalonar matriz dos vetores
function escalonar(){
	var a,b,c,d,e,f;
	var b0 = false;

	if(anguloMedio()==anguloW){
		if(anguloV>anguloU){	
			a=vx;
			b=vy;
			c=ux;
			d=uy;
			e=wx;
			f=wy;
		}
		else{
			a=ux;
			b=uy;
			c=vx;
			d=vy;
			e=wx;
			f=wy;
		}
	}
	else if(anguloMedio()==anguloU){
		if(anguloV>anguloW){
			a=vx;
			b=vy;
			c=wx;
			d=wy;
			e=ux;
			f=uy;
		}
		else{
			a=wx;
			b=wy;
			c=vx;
			d=vy;
			e=ux;
			f=uy;
		}
	}
	else{
		if(anguloU>anguloW){
			a=ux;
			b=uy;
			c=wx;
			d=wy;
			e=vx;
			f=vy;
		}
		else{
			a=wx;
			b=wy;
			c=ux;
			d=uy;
			e=vx;
			f=vy;
		}
	}
	var c0 = [a,b];
	var c1 = [c,d];
	var c2 = [e,f];

	if(a==0){
		//Trocar linhas
		c0 = [b,a];
		c1 = [d,c];
		c2 = [f,e];
		b0 = true;
	}
	else if(b==0)
		b0=true;
	//Primeiro pivor
	c1[0]/=c0[0];
	c2[0]/=c0[0];
	c0[0]/=c0[0];
	if(b0==false){
		//(1,0) = 1
		c1[1]/=c0[1];
		c2[1]/=c0[1];
		c0[1]/=c0[1];
		//Zerar (1,0)
		c0[1]-=c0[0];
		c1[1]-=c1[0];
		c2[1]-=c2[0];
	}	
	//Segundo pivor
	c2[1]/=c1[1];
	c1[1]/=c1[1];

	if(c1[0]!=0){
		c2[0]=c2[0]-(c1[0]*c2[1]);
		c1[0]=c1[0]-(c1[0]*c1[1]);
	}
	alpha = c2[0];
	beta = c2[1]; 
}

function anguloMedio(){
	if(anguloV>anguloW && anguloV<anguloU || anguloV<anguloW && anguloV>anguloU)
		return anguloV;
	else if(anguloU>anguloV && anguloU<anguloW || anguloU<anguloV && anguloU>anguloW)
		return anguloU;
	else if(anguloW>anguloV && anguloW<anguloU || anguloW<anguloV && anguloW>anguloU)
		return anguloW;
}

