var param = {
	"id":"ggb",
	"width":1200,
	"height":500,
	"showToolBar":false,
	"borderColor":null,
	"showMenuBar":false,
	"showAlgebraInput":false,
	"showResetIcon":false,
	"enableLabelDrags":false,
	"enableShiftDragZoom":true,
	"enableRightClick":false,
	"capturingThreshold":null,
	"showToolBarHelp":false,
	"errorDialogsActive":false,
	"useBrowserForJS":true,
	"ggbBase64":""};
var ggbApplet = new GGBApplet(param, '5.0');
ggbApplet.setHTML5Codebase('https://app.geogebra.org/5.0/web3d/');
window.onload = function() {
	ggbApplet.inject('geogebra','preferHTML5');
}

ggb = document.ggb;
quadrica = 'cone';
plano = 'planoZ';
ggbInit = false;

function ggbOnInit(){
	ggb = document.ggb;
	ggb.enableRightClick(false);
	ggb.enableLabelDrags(false);
	document.getElementById('quadrica').value = 'cone';
	document.getElementById('plano').value = 'planoZ';
	document.getElementById('range').value = 0;
	ggb.setValue('valor',0);
	setEscala();
	setFormula();
	ggbInit = true;
}

function setValorPlano(){
	var valor = document.getElementById('valorRange');
	var range = document.getElementById('range');
	valor.innerHTML = range.value;
	if(ggbInit)
		ggb.setValue('valor',range.value);
}

function setQuadrica(){
	if(ggbInit){	
		ggb.setVisible(quadrica,false);
		quadrica = document.getElementById('quadrica').value;
		ggb.setVisible(quadrica,true);
		setIntersecao();
		setEscala();
	}
	setFormula();
}

function setPlano(){
	if(ggbInit){	
		ggb.setVisible(plano,false);
		plano = document.getElementById('plano').value;
		ggb.setVisible(plano,true);
		setIntersecao();
		setEscala();
	}
}

function setEscala(){
	ggb.setCoordSystem(-6,6,-6,6,-6,6);
	ggb.setCoordSystem(-6.5,6.5,-6.5,6.5);
}

function setFormula(){
	var formula = document.querySelector('#formula');
	var quadrica = document.getElementById('quadrica').value;
	switch (quadrica){
		case 'cone':
		formula.innerHTML = '`rArr x^2/a^2+y^2/b^2-z^2/c^2=0`';
		break;
		case 'elipsoide':
		formula.innerHTML = '`rArr x^2/a^2+y^2/b^2+z^2/c^2=1`';
		break;
		case 'paraboloideEliptico':
		formula.innerHTML = '`rArr x^2/a^2+y^2/b^2-z=0`';
		break;
		case 'paraboloideHiperbolico':
		formula.innerHTML = '`rArr x^2/a^2-y^2/b^2-z=0`';
		break;
		case 'paraboloideRevolucao':
		formula.innerHTML = '`rArr x^2/a^2+y^2/a^2-z=0`';
		break;
		case 'hiperboloideUmaFolha':
		formula.innerHTML = '`rArr x^2/a^2+y^2/b^2-z^2/c^2=1`';
		break;
		case 'hiperboloideDuasFolhas':
		formula.innerHTML = '`rArr -x^2/a^2-y^2/b^2+z^2/c^2=1`';
		break;
		case 'cilindroEliptico':
		formula.innerHTML = '`rArr x^2/a^2+y^2/b^2=1, -c<=z<=c`';
		break;
		case "cilindroHiperbolico":
		formula.innerHTML = '`rArr x^2/a^2-y^2/b^2=1, -c<=z<=c`';
		break;
		case 'cilindroParabolico':
		formula.innerHTML = '`rArr x^2/a^2+by=1, -c<=z<=c`';
		break;
	}
	MathJax.Hub.Typeset(); //atualizar notacao asciimath
}

function setIntersecao(){
	if(quadrica!='elipsoide')
		setNaoElipsoide();
	else
		setElipsoide();
}

function setElipsoide(){
	ggb.setVisible('interseccao',false);
	if(plano=='planoZ'){
		ggb.evalCommand('projecao = Translate[interZe, Vector[(0, 0, -valor)]]');
		ggb.setVisible('interZe',true);
		ggb.setVisible('interYe',false);
		ggb.setVisible('interXe',false);
		}
	else if(plano=='planoY'){
		ggb.evalCommand('projecao = Rotate[Translate[interYe,(0,-valor,0)],-90°,xAxis]');
		ggb.setVisible('interYe',true);
		ggb.setVisible('interZe',false);
		ggb.setVisible('interXe',false);
	}
	else if(plano=='planoX'){
		ggb.evalCommand('projecao = Rotate[Rotate[Translate[interXe,(-valor,0,0)],-90°,yAxis],-90°,zAxis]');
		ggb.setVisible('interXe',true);
		ggb.setVisible('interZe',false);
		ggb.setVisible('interYe',false);
	}
}

function setNaoElipsoide(){
	ggb.setVisible('interseccao',true);
	ggb.setVisible('interYe',false);
	ggb.setVisible('interZe',false);
	ggb.setVisible('interXe',false);
	ggb.evalCommand('interseccao = Intersect['+plano+', '+quadrica+']');
	if(plano=='planoZ'){
		ggb.evalCommand('projecao = Translate[interseccao, Vector[(0, 0, -valor)]]');
	}
	else if(plano=='planoY'){
		ggb.evalCommand('projecao = Rotate[Translate[interseccao,(0,-valor,0)],-90°,xAxis]');
	}
	else if(plano=='planoX'){
		ggb.evalCommand('projecao = Rotate[Rotate[Translate[interseccao,(-valor,0,0)],-90°,yAxis],-90°,zAxis]');
	}
}