var parameters = {"id":"FvpzC6hc","width":500,"height":500,"showToolBar":false,"borderColor":null,"showMenuBar":false,"showAlgebraInput":false,"showResetIcon":false,"enableLabelDrags":false,"enableShiftDragZoom":true,"enableRightClick":false,"capturingThreshold":null,"showToolBarHelp":false,"errorDialogsActive":false,"useBrowserForJS":false,"ggbBase64":""};
var applet = new GGBApplet(parameters, '5.0');

window.onload = function() {
	applet.inject('applet_container');
	ggb = document.FvpzC6hc;
	dados = document.getElementById('dados');
}

function figurinha(){
	ggb = document.FvpzC6hc;
	figura = dados.elements[0].value;
	visibilidadeFalse();
	visibilidadeTrue(figura);
}

function select(elemento,escalar){
	var valor = elemento.value;
	var escalar = document.getElementById(escalar);
	if(valor=='dilatacao' || valor=='cisalhamento'){
		escalar.style.display = 'inline';
		escalar.setAttribute('placeholder','Escalar');
	}
	else if(valor=='rotacao'){
		escalar.style.display = 'inline';
		escalar.setAttribute('placeholder','Graus');
	}
	else
		escalar.style.display = 'none';
}

function executar(){
	var operador1 = dados.elements[1].value;
	var operador2 = dados.elements[3].value;
	var operador3 = dados.elements[5].value;
	var escalar1 = dados.elements[2].value;
	var escalar2 = dados.elements[4].value;
	var escalar3 = dados.elements[6].value;
	ggb = document.FvpzC6hc;
	figura = dados.elements[0].value;
	var tempo = 1000;
	//alert(isNaN(escalar1));
	//entradaInvalida(operador1,operador2,operador3,escalar1,escalar2,escalar3);
	operacao(operador1,escalar1);
	if(operador2!="nenhum"){
		setTimeout(function(){operacao(operador2,escalar2);},tempo);
		tempo*=2;
	}
	if(operador3!='nenhum')
		setTimeout(function(){operacao(operador3,escalar3);},tempo);
}

function operacao(operador,escalar){
	visibilidadeLinha();
	if(operador=='nenhum'){	
		return;
	}
	if(operador=='reflexaoX'){
		if(figura=='vetor')
			reflexaoX('A');
		else if(figura=='triangulo'){
			reflexaoX('A');
			reflexaoX('B');
			reflexaoX('C');
		}
		else if(figura=='retangulo'){
			reflexaoX('A');
			reflexaoX('B');
			reflexaoX('C');
			reflexaoX('D');
		}
		else if(figura=='pentagono'){
			reflexaoX('A');
			reflexaoX('B');
			reflexaoX('C');
			reflexaoX('D');
			reflexaoX('E');
		}
		else if(figura=='hexagono'){
			reflexaoX('A');
			reflexaoX('B');
			reflexaoX('C');
			reflexaoX('D');
			reflexaoX('E');
			reflexaoX('F');
		}
	}
	else if(operador=='reflexaoY'){
		if(figura=='vetor')
			reflexaoY('A');
		else if(figura=='triangulo'){
			reflexaoY('A');
			reflexaoY('B');
			reflexaoY('C');
		}
		else if(figura=='retangulo'){
			reflexaoY('A');
			reflexaoY('B');
			reflexaoY('C');
			reflexaoY('D');
		}
		else if(figura=='pentagono'){
			reflexaoY('A');
			reflexaoY('B');
			reflexaoY('C');
			reflexaoY('D');
			reflexaoY('E');
		}
		else if(figura=='hexagono'){
			reflexaoY('A');
			reflexaoY('B');
			reflexaoY('C');
			reflexaoY('D');
			reflexaoY('E');
			reflexaoY('F');
		}
	}
	else if(operador=='dilatacao'){
		if(figura=='vetor')
			dilatacao('A',escalar);
		else if(figura=='triangulo'){
			dilatacao('A',escalar);
			dilatacao('B',escalar);
			dilatacao('C',escalar);
		}
		else if(figura=='retangulo'){
			dilatacao('A',escalar);
			dilatacao('B',escalar);
			dilatacao('C',escalar);
			dilatacao('D',escalar);
		}
		else if(figura=='pentagono'){
			dilatacao('A',escalar);
			dilatacao('B',escalar);
			dilatacao('C',escalar);
			dilatacao('D',escalar);
			dilatacao('E',escalar);
		}
		else if(figura=='hexagono'){
			dilatacao('A',escalar);
			dilatacao('B',escalar);
			dilatacao('C',escalar);
			dilatacao('D',escalar);
			dilatacao('E',escalar);
			dilatacao('F',escalar);
		}
	}
	else if(operador=='rotacao'){
		if(figura=='vetor')
			rotacao('A',escalar);
		else if(figura=='triangulo'){
			rotacao('A',escalar);
			rotacao('B',escalar);
			rotacao('C',escalar);
		}
		else if(figura=='retangulo'){
			rotacao('A',escalar);
			rotacao('B',escalar);
			rotacao('C',escalar);
			rotacao('D',escalar);
		}
		else if(figura=='pentagono'){
			rotacao('A',escalar);
			rotacao('B',escalar);
			rotacao('C',escalar);
			rotacao('D',escalar);
			rotacao('E',escalar);
		}
		else if(figura=='hexagono'){
			rotacao('A',escalar);
			rotacao('B',escalar);
			rotacao('C',escalar);
			rotacao('D',escalar);
			rotacao('E',escalar);
			rotacao('F',escalar);
		}
	}
	else if(operador=='cisalhamento'){
		
	}
}

function reflexaoX(P){
	var p = P.toLowerCase();
	ggb.evalCommand(p+'nx=x('+P+')');
	ggb.evalCommand(p+'tx=x('+P+')');
	ggb.evalCommand(p+'ny=-y('+P+')');
	ggb.evalCommand(p+'ty=-y('+P+')');
}

function reflexaoY(P){
	var p = P.toLowerCase();
	ggb.evalCommand(p+'nx=-x('+P+')');
	ggb.evalCommand(p+'tx=-x('+P+')');
	ggb.evalCommand(p+'ny=y('+P+')');
	ggb.evalCommand(p+'ty=y('+P+')');
}

function dilatacao(P,escalar){
	var p = P.toLowerCase();
	ggb.evalCommand(p+'nx='+escalar+'*x('+P+')');
	ggb.evalCommand(p+'tx='+escalar+'*x('+P+')');
	ggb.evalCommand(p+'ny='+escalar+'*y('+P+')');
	ggb.evalCommand(p+'ty='+escalar+'*y('+P+')');
}

