var a = 'aElipse';

var parameters = {"id":"NZzb5uPm","width":900,"height":500,"showToolBar":false,"borderColor":null,"showMenuBar":false,"showAlgebraInput":false,"showResetIcon":false,"enableLabelDrags":false,"enableShiftDragZoom":true,"enableRightClick":false,"capturingThreshold":null,"showToolBarHelp":false,"errorDialogsActive":false,"useBrowserForJS":false,"ggbBase64":""};
var applet = new GGBApplet(parameters, '5.0');

window.onload = function() {
	applet.inject('applet_container');
}

function figura(fig){
	conica = fig;
	ggb = document.NZzb5uPm;

	btnActive();
	setggb();
}

function btnActive(){
	document.getElementById('btn-elipse').className = 'btn btn-default';
	document.getElementById('btn-hiperbole').className = 'btn btn-default';
	document.getElementById('btn-parabola').className = 'btn btn-default';
	document.getElementById('btn-'+conica).className = 'btn btn-default active';
}

function setggb(){
	ggb.renameObject('a',a);
	
	if(conica=='elipse'){
		ggb.setVisible('elipse',true);
		ggb.setVisible('hiperbole',false);
		ggb.setVisible('parabola',false);
		ggb.setVisible('diretriz',false);
		ggb.setVisible('aElipse',true);
		ggb.setVisible('aHiperbole',false);
		ggb.setVisible('A',false);
		ggb.setVisible('B',false);
		ggb.setVisible('f',false);
		ggb.setVisible('f1',true);
		ggb.setVisible('f2',true);
		ggb.setVisible('p',true);
		ggb.setVisible('d_1',true);
		ggb.setVisible('d_2',true);
		ggb.setVisible('s1',false);
		ggb.setVisible('s2',false);
		ggb.setVisible('definicao',true);
		ggb.setVisible('solucao',true);

		ggb.evalCommand('aElipse = 3');
		ggb.evalCommand('f1 = Point[{-2,0}]');
		ggb.evalCommand('f2 = Point[{2,0}]');
		ggb.evalCommand('p = Point[elipse]');
		ggb.evalCommand('definicao = Text[\"d_1 + d_2 = 2a\"]');
		ggb.evalCommand('solucao = Text[d_1 +\" + \"+ d_2 +\" = \"+ (2aElipse)]');

		a = 'aElipse';
		ggb.renameObject('aElipse','a');
	}
	else if(conica=='hiperbole'){
		ggb.setVisible('elipse',false);
		ggb.setVisible('hiperbole',true);
		ggb.setVisible('parabola',false);
		ggb.setVisible('diretriz',false);
		ggb.setVisible('aElipse',false);
		ggb.setVisible('aHiperbole',true);
		ggb.setVisible('A',false);
		ggb.setVisible('B',false);
		ggb.setVisible('f',false);
		ggb.setVisible('f1',true);
		ggb.setVisible('f2',true);
		ggb.setVisible('p',true);
		ggb.setVisible('d_1',true);
		ggb.setVisible('d_2',true);
		ggb.setVisible('s1',false);
		ggb.setVisible('s2',false);
		ggb.setVisible('definicao',true);
		ggb.setVisible('solucao',true);

		ggb.evalCommand('aHiperbole = 1');
		ggb.evalCommand('f1 = Point[{-2,0}]');
		ggb.evalCommand('f2 = Point[{2,0}]');
		ggb.evalCommand('p = Point[hiperbole]');
		ggb.evalCommand('definicao = Text[\"|d_1 - d_2| = 2a\"]');
		ggb.evalCommand('solucao = Text[\"|\" + d_1 +\" - \"+ d_2 +\"| = \"+ (2aHiperbole)]');

		a = 'aHiperbole';
		ggb.renameObject('aHiperbole','a');
	}
	else if(conica=='parabola'){
		ggb.setVisible('elipse',false);
		ggb.setVisible('hiperbole',false);
		ggb.setVisible('parabola',true);
		ggb.setVisible('diretriz',true);
		ggb.setVisible('aElipse',false);
		ggb.setVisible('aHiperbole',false);
		ggb.setVisible('A',true);
		ggb.setVisible('B',true);
		ggb.setVisible('f',true);
		ggb.setVisible('f1',false);
		ggb.setVisible('f2',false);
		ggb.setVisible('p',true);
		ggb.setVisible('d_1',false);
		ggb.setVisible('d_2',false);
		ggb.setVisible('s1',true);
		ggb.setVisible('s2',true);
		ggb.setVisible('definicao',false);
		ggb.setVisible('solucao',false);

		ggb.evalCommand('A = (-1,-1)');
		ggb.evalCommand('B = (1,-1)');
		ggb.evalCommand('f = Point[{0,1}]');
		ggb.evalCommand('p = Point[parabola]');
	}
	ggb.setCoordSystem(-6,6,-3,3);
}